import '../res/style/App.css';
import Project from "../components/portfolio/Project";
import {projects} from "../constants/projects.js";
import {To} from "react-router-dom";
import Button from "@mui/material/Button";
import {navButtonStyle} from "../res/style/inputStyles";
import {HashLink} from "react-router-hash-link";

const Portfolio = () => {
    const {web, mobile, embedded, ml, media} = projects;

    function scrollWithOffset(el: HTMLElement) {
        const yCoordinate = el.getBoundingClientRect().top + window.pageYOffset;
        window.scrollTo({ top: yCoordinate - props.navHeight, behavior: 'smooth' });
    }

    return (
        <div className="Body">
            {dropdown ?
                (
                    <ul className="nav-submenu"
                        onMouseEnter={() => showDropdown()}
                        onMouseLeave={() => hideDropdown()}
                    >
                        {item.submenu.map((submenuItem: { destination: To; title: string; }) => (
                            <li key={submenuItem.title} className="nav-submenu-item">
                                <Button variant="outlined"
                                        sx={navButtonStyle}
                                        onMouseEnter={() => cursorChangeHandler("hovered")}
                                        onMouseLeave={() => cursorChangeHandler("")}>
                                    <HashLink smooth
                                              to={submenuItem.destination}
                                              scroll={el => scrollWithOffset(el)}
                                    >
                                        {submenuItem.title}
                                    </HashLink>
                                </Button>
                            </li>
                        ))}
                    </ul>
                ) : <></>
            }
            <h1 id="web">Web</h1>
            {web.map((project: any) =>
                <Project key={project.title} project={project} projectType={"web"} />
            )}
            <h1 id="mobile">Mobile Apps</h1>
            {mobile.map((project: any) =>
                <Project key={project.title} project={project} projectType={"mobile"} />
            )}
            <h1 id="embedded">Embedded Systems</h1>
            {embedded.map((project: any) =>
                <Project key={project.title} project={project} projectType={"embedded"} />
            )}
            <h1 id="ml">Machine Learning</h1>
            {ml.map((project: any) =>
                <Project key={project.title} project={project} projectType={"ml"} />
            )}
            <h1 id="media">Multimedia Processing</h1>
            {media.map((project: any) =>
                <Project key={project.title} project={project} projectType={"media"} />
            )}
        </div>
    );
}

export default Portfolio;
