import {Link, To} from "react-router-dom";
import {HashLink} from "react-router-hash-link";
import {useContext, useState} from "react";
import {MouseContext} from "../../context/MouseContext";
import Button from "@mui/material/Button";
import {navButtonStyle} from "../../res/style/inputStyles";

const NavItem = (props: { item: any; navHeight: number; setSubNav: any; }) => {
    const {item} = props;
    const { cursorChangeHandler } = useContext(MouseContext);
    const [dropdown, setDropdown] = useState(false);

    function scrollWithOffset(el: HTMLElement) {
        const yCoordinate = el.getBoundingClientRect().top + window.pageYOffset;
        window.scrollTo({ top: yCoordinate - props.navHeight, behavior: 'smooth' });
    }

    function showDropdown() {
        props.setSubNav(item.submenu)
    }

    function hideDropdown() {
        props.setSubNav(null)
    }

    return (
        <li className="nav-menu-item">
            <Button variant="outlined"
                    sx={navButtonStyle}
                    onMouseEnter={() => {
                        showDropdown();
                        cursorChangeHandler("hovered");
                    }}
                    onMouseLeave={() => {
                        hideDropdown();
                        cursorChangeHandler("");
                    }}>
                <Link to={item.destination}>
                    {item.title}
                </Link>
            </Button>
            {dropdown ?
                (
                    <ul className="nav-submenu"
                        onMouseEnter={() => showDropdown()}
                        onMouseLeave={() => hideDropdown()}
                    >
                        {item.submenu.map((submenuItem: { destination: To; title: string; }) => (
                            <li key={submenuItem.title} className="nav-submenu-item">
                                <Button variant="outlined"
                                        sx={navButtonStyle}
                                        onMouseEnter={() => cursorChangeHandler("hovered")}
                                        onMouseLeave={() => cursorChangeHandler("")}>
                                    <HashLink smooth
                                              to={submenuItem.destination}
                                              scroll={el => scrollWithOffset(el)}
                                    >
                                        {submenuItem.title}
                                    </HashLink>
                                </Button>
                            </li>
                        ))}
                    </ul>
                ) : <></>
            }
        </li>
    );
}

export default NavItem;
