import {Link, To} from "react-router-dom";
import {HashLink} from "react-router-hash-link";
import {useContext, useState} from "react";
import {MouseContext} from "../providers/MouseContext";
import Button from "@mui/material/Button";

const NavItem = (props: { item: any; }) => {
    const {item} = props;
    const { cursorChangeHandler } = useContext(MouseContext);

    const [dropdown, setDropdown] = useState(false);

    function showDropdown() {
        if (item.submenu) setDropdown(true)
    }

    function hideDropdown() {
        if (item.submenu) setDropdown(false)
    }

    return (
        <li className="nav-menu-item">
            <Link className="nav-item-text"
                  to={item.destination}
                  onMouseEnter={() => {
                      showDropdown();
                      cursorChangeHandler("hovered");
                  }}
                  onMouseLeave={() => {
                      hideDropdown();
                      cursorChangeHandler("");
                  }}
            >
                {item.title}
            </Link>
            {dropdown ?
                (
                    <ul className="nav-submenu"
                        onMouseEnter={() => {
                            showDropdown();
                            cursorChangeHandler("hovered");
                        }}
                        onMouseLeave={() => {
                            hideDropdown();
                            cursorChangeHandler("");
                        }}
                    >
                        {item.submenu.map((submenuItem: { destination: To; title: string; }) => (
                            <li key={submenuItem.title}
                                className="nav-submenu-item"
                                onMouseEnter={() => cursorChangeHandler("hovered")}
                                onMouseLeave={() => cursorChangeHandler("")}>
                                <Button variant="outlined">
                                    <HashLink
                                              to={submenuItem.destination}>
                                        {submenuItem.title}
                                    </HashLink>
                                </Button>
                            </li>
                        ))}
                    </ul>
                ) : <></>
            }
        </li>
    );
}

export default NavItem;
