import '../../res/style/Navigation.css';
import navigation from "../../constants/navigation";
import NavItem from "./NavItem";
import SocialBar from "./SocialBar";
import {MouseContext} from "../../context/MouseContext";
import {useContext, useLayoutEffect, useRef, useState} from "react";
import {To} from "react-router-dom";
import Button from "@mui/material/Button";
import {navButtonStyle} from "../../res/style/inputStyles";
import {HashLink} from "react-router-hash-link";

interface IProps {
    height: number,
    setHeight: (height: number) => void,
}

const NavBar = (props: IProps) => {
    const { cursorChangeHandler } = useContext(MouseContext);
    const [subNav, setSubNav] = useState<any>();
    const ref = useRef<HTMLDivElement>(null);

    useLayoutEffect(() => {
        if (!ref.current || !ref.current.clientHeight) {
            return;
        }

        props.setHeight(ref.current.clientHeight);
    }, []);

    function hideDropdown() {
        if (subNav) setDropdown(null)
    }

    return (
        <div ref={ref} className="nav-bar">
            <div className="nav-content">
                <div className="nav-left">
                    <a href="/"
                       className="nav-logo-text"
                       onMouseEnter={() => cursorChangeHandler("hovered")}
                       onMouseLeave={() => cursorChangeHandler("")}
                    >
                        noah.coffee
                    </a>
                </div>
                <div className="nav-right">
                    <SocialBar />
                    <nav className="nav-container">
                        <ul className="nav-menu">
                            {navigation.map((item) =>
                                <NavItem key={item.title}
                                         item={item}
                                         navHeight={props.height}
                                         setSubNav={setSubNav}/>
                            )}
                        </ul>
                        <ul className="nav-submenu"
                            onMouseLeave={() => hideDropdown()}
                        >
                            {subNav.map((submenuItem: { destination: To; title: string; }) => (
                                <li key={submenuItem.title} className="nav-submenu-item">
                                    <Button variant="outlined"
                                            sx={navButtonStyle}
                                            onMouseEnter={() => cursorChangeHandler("hovered")}
                                            onMouseLeave={() => cursorChangeHandler("")}>
                                        <HashLink smooth
                                                  to={submenuItem.destination}
                                                  scroll={el => scrollWithOffset(el)}
                                        >
                                            {submenuItem.title}
                                        </HashLink>
                                    </Button>
                                </li>
                            ))}
                        </ul>
                    </nav>
                </div>
            </div>
        </div>
    )
}

export default NavBar;
