import React, {createContext, useState, useEffect, Dispatch, SetStateAction} from "react";

/*

    Mouse position

 */

export function useMousePosition() {
    const [position, setPosition] = useState({
        clientX: 0,
        clientY: 0,
    });

    const updatePosition = (event: { pageX: any; pageY: any; clientX: any; clientY: any; }) => {
        const { pageX, pageY, clientX, clientY } = event;

        setPosition({
            clientX,
            clientY,
        });
    };

    useEffect(() => {
        document.addEventListener("mousemove", updatePosition, false);
        document.addEventListener("mouseenter", updatePosition, false);

        return () => {
            document.removeEventListener("mousemove", updatePosition);
            document.removeEventListener("mouseenter", updatePosition);
        };
    }, []);

    return position;
};

/*

    Cursor Context

 */

interface ICursorContext {
    value: any,
}

interface ICursor {
    active: boolean
}

export const CursorContext = createContext<ICursorContext | undefined>(undefined)

const defaultCursorState: ICursor = { active: false };



function CursorContextProvider(props: any) {
    const [cursor, setCursor] = useState<ICursorContext>({ active: false });

    return (
        <CursorContext.Provider value={[cursor, setCursor]}>
            {props.children}
        </CursorContext.Provider>
    );
};

export default CursorContextProvider;
